tPhrase, getState });        };        assistant = init();        assistant.on('start', () => { logger.log('SmartApp started'); });        assistant.on('data', event =>        {            // Set your action or data hooks            if (!event.type) {                // Use invariants to prevent errors on Sber Portal                return;            }            // FIXME Add event handler for closing the app and use "assistant.close()" inside it;            if (event.type === 'character') { character = event.character.id; }            if (event.type === 'smart_app_data') {                if (event.smart_app_data.type == 'changeword') {                    news();                } else if (event.smart_app_data.type == 'changemode') {                    toggle();                } else if (event.smart_app_data.type === 'help') {                    helpIsAsked = true;                } else if (event.smart_app_data.type === 'restart') {                    reset();                }            }            if (event.type === 'navigation') { let word = event.navigation.command; }            logger.log(event);        });    });    let game = { score: 0, best: 0,};    const reset = () =>    {        rand = dict[Math.floor(Math.random() * dict.length)]        game.score = 0;        game.best = 0;    };    function toggle() {        if (!visible)        {            window.document.body.classList.toggle('hard-mode')            if (com == "Базовый") com = "Сложный";            else com = "Базовый";            rand = dict[Math.floor(Math.random() * dict.length)]        }        else {            visible = false;            setTimeout(function() {                window.document.body.classList.toggle('hard-mode');                if (com == "Базовый") {                    com = "Сложный";                }                else {                    com = "Базовый"                } }, 500);            rand = dict[Math.floor(Math.random() * dict.length)]        }    }    function news() {        if (com == "Базовый") {            dict = dictionary;            rand = dict[Math.floor(Math.random() * dict.length)]        }        else {            dict = dictionary2;            rand = dict[Math.floor(Math.random() * dict.length)]        }        if (visible) {            visible = false;            setTimeout(function() { visible = true; }, 1000);        }        else {            setTimeout(function() { visible = true; }, 100);        }    }    function gcount() { game.best+=1; game.score+=1; }    function badcount() { game.best+=1; }    let visible = false;    function typewriter(node, { speed = 1 }) {        const valid = (            node.childNodes.length === 1 &&            node.childNodes[0].nodeType === Node.TEXT_NODE        );        if (!valid) {            throw new Error(`This transition only works on elements with a single text node child`);        }        const text = node.textContent;        logger.log(text);        const duration = text.length / (speed * 0.05);        return {            duration,            tick: t => {                const i = ~~(text.length * t);                node.textContent = text.slice(0, i);            }        };    }</script><div class="container">    <div class="heading">        <h1 class="title"><a href='/'>Cлова для Крокодила</a></h1>        <div class="scores-container">            <div class="score-container">{game.score}</div>            <div class="best-container">{game.best}</div>        </div>    </div>    <div class="game-intro">        <button class="restart-button" on:click={reset}>Заново</button>        <button class= "help-button" on:click={() => helpIsAsked = true}>Помощь</button>        <button class = "type" on:click={toggle}>Режим</button>        <p> {com} </p>    </div>    <div class="game-container">        {#if visible}            <p class = "answer" transition:typ